query:
query BatchAdvisories {
  adv1: securityAdvisory(ghsaId: "GHSA-1111-2222-3333") {
    ghsaId
    summary
    description
    publishedAt
    updatedAt
    severity
    references {
      url
    }
    identifiers {
      type
      value
    }
  }
